def home(request):
    lang = request.GET.get('lang', 'en')
    if lang == 'en':
        crop_plans = crop_plans_en
    elif lang == 'hi':
        crop_plans = crop_plans_hi
    elif lang == 'mr':
        crop_plans = crop_plans_mr
    else:
        crop_plans = crop_plans_en
    result = None
    plan = None
    form = CropForm(request.POST if request.method == 'POST' else None)
    if request.method == 'POST' and form.is_valid():
        data = [form.cleaned_data[field] for field in ['N', 'P', 'K', 'temperature', 'humidity', 'ph', 'rainfall']]
        prediction = model.predict([data])[0]
        result = f"Recommended Crop: {prediction}"
        plan = crop_plans.get(prediction.lower())
    return render(request, 'DX_APP/home.html', {'form': form, 'result': result, 'plan': plan, 'lang': lang})


